export class CECoreHello extends HTMLElement {
  constructor() {
    super();
    let shadow = this.attachShadow({ mode: "closed" });

    // clone the template to create a fragment
    let coreHelloFrag = document.getElementById("CECoreHelloT").content.cloneNode(true);
    let lang = this.getAttribute("lang");
    // default to English
    let langVal = lang === null ? "en" : lang.valueOf();
    let greeting = langVal === "es" ? "Hola Mundo" : 
      langVal === "pt" ? "Olá Mundo" : 
        "Hello World";
    // set paragraph text to the greeting and whatever HTML was originally inside the <ce-core-hello> tag
    coreHelloFrag.querySelector("p").innerHTML = `${greeting} ${this.innerHTML}`;
    // this isn't necessary, but it gets rid of the original HTML because it is now in the p element
    this.innerHTML = "";

    // cycle through colors of rainbow every 200 ms
    let colorI = 0;
    let colors = ["red", "orange", "yellow", "green", "blue", "indigo", "violet"];
    if(this.getAttribute("rainbow") !== null) {
      setInterval(() => {
        this.style.color = colors[colorI];
        colorI = (colorI + 1) % colors.length;
      }, 200);
    }

    shadow.appendChild(coreHelloFrag);
  }
}
customElements.define("ce-core-hello", CECoreHello);

/**
 * A web component that adds "Hello World" to the content
 */
class corehello extends HTMLElement {

  /**
   * Construct core-hello with rainbow effect and translation
   * @constructor
   */
  constructor() {
    super();

    var lan = this.getAttribute("lang"); //Get lang Attribute
    var name = this.innerHTML; //Get the name after text "Hello World - "
    const shadowRoot = this.attachShadow({mode: "open"}); //shadow DOM


    var template = document.getElementById("major-template");
    var clone = document.importNode(template.content, true);
    var td = clone.querySelector("p");

    //Language texts cases
    if(lan == "English") {
      td.textContent = "Hello World - "+name;
    }
    else if(lan == "Spanish") {
      td.textContent = "Hola Mundo - "+name;
    }
    else if(lan == "Chinese") {
      td.textContent = "你好,世界 - "+name;
    }
    else if(lan == "Team7") {
      td.textContent = "We love "+name;
    }
    shadowRoot.appendChild(clone);
  }
}
//Define Component
window.customElements.define("core-hello", corehello);

