  }

  /** @type {boolean} */
  set hideComment(value) {
    const isHiddenComment = Boolean(value);
    if (isHiddenComment)
      this.setAttribute("hide-comment", "");
    else
      this.removeAttribute("hide-comment");
  }

  /** @type {boolean} */
  get showTags() {
